root.template:
	//! RTC Oscillator Enable
 	static constexpr uint32_t CR_OSCE_M = ($(CR_OSCE)<<RTC_CR_OSCE_SHIFT);

 	//! RTC 32kHz Clock Output
 	static constexpr uint32_t CR_CLKO_M = ($(CR_CLKO)<<RTC_CR_CLKO_SHIFT);

 	//! Update Mode
 	static constexpr uint32_t CR_UM_M = ($(CR_UM)<<RTC_CR_UM_SHIFT);

 	//! Supervisor Access
 	static constexpr uint32_t CR_SUP_M = ($(CR_SUP)<<RTC_CR_SUP_SHIFT);

 	//! Wakeup Pin Enable
 	static constexpr uint32_t CR_WPE_M = ($(CR_WPE)<<RTC_CR_WPE_SHIFT);

 	//! Oscillator load capacitance
 	static constexpr uint32_t CR_SCP_M = ($(CR_SCP)<<RTC_CR_SC16P_SHIFT);
