peripheralPage.setTemplate:
\t/**
\t * Allocate %description
\t * The GPIO function on the associated pin is disabled
\t *
%paramDescription
\t */
\tstatic void allocateAnalogueInput(%params) {
\t   %register = %register | %paramName0;
\t}
\t
\t/**
\t * Release %description
\t * The GPIO function on the associated pin is enabled
\t *
%paramDescription
\t */
\tstatic void releaseAnalogueInput(%params) {
\t   %register = %register & ~%paramName0;
\t}
\t\n

for.values:
acmp_cs_ace:Enable;
acmp_cs_hyst:Hysterisis;
acmp_cs_acie:Interrupt;
acmp_cs_acope:PinEnable;
acmp_cs_acmod:EdgeSensitivity;
acmp_c0_acpsel:PositiveInput;
acmp_c0_acnsel:NegativeInput;
acmp_c1_dacen:Dac;
acmp_c1_dacref:DacLevel

peripheralPage.template:

\t/** Callback function for ISR */
\tstatic CallbackFunction sCallback;
\t
\t/**
\t * Callback to catch unhandled interrupt
\t */
\tstatic void unhandledCallback($(irq_dummy_parameters)) {
\t   setAndCheckErrorCode(E_NO_HANDLER);
\t}
\t\n


\t/**
\t * Set interrupt callback function.
\t *
\t * @param[in]  acmpCallback Callback function to execute on interrupt
\t */
\tstatic void setCallback(CallbackFunction acmpCallback) {
\t   if (acmpCallback == nullptr) {
\t      acmpCallback = unhandledCallback;
\t   }
\t   usbdm_assert(
\t         (sCallback == unhandledCallback) || (sCallback == acmpCallback),
\t         "Handler already set");
\t   sCallback = acmpCallback;
\t}
\t
\t/**
\t * $(_BASENAME) interrupt handler -  Calls $(_BASENAME) callback
\t */
\tstatic void irqHandler() {
\t
\t   //.....IRQ handler code here..........
\t
\t   // Execute call-back
\t   sCallback($(irq_call));
\t   return;
\t}
\t\n

\t/**
\t * Configure with default settings.
\t * Configuration determined from Configure.usbdmProject
\t */
\tstatic inline void defaultConfigure() {
\t
\t   // Update settings
\t   configure(Info::DefaultInitValue);
\t}
\t\n

\t   // ..........  Regs to init .......... ;
\t   acmp->CS    = init.cs;
\t   acmp->C0    = init.c0;
\t   acmp->C1    = init.c1;
\t   acmp->C2    = init.c2;
\t}
\t\n

for.values:
acmp_cs_ace;
acmp_cs_hyst;
acmp_cs_acie;
acmp_cs_acope;
acmp_cs_acmod;
acmp_c0_acpsel;
acmp_c0_acnsel;
acmp_c1_dacen;
acmp_c1_dacref
