peripheralPage.template:
\t/**
\t *  Get input clock frequency for ADC
\t *  
\t *  @param adcClockSource Clock source chosen for ADC
\t *
\t *  @return Frequency in Hz
\t */
\tstatic unsigned getInputClockFrequency(AdcClockSource adcClockSource) {
\t   switch (adcClockSource) {
\t      case AdcClockSource_Bus:
\t         return SystemBusClock;
\t      case AdcClockSource_Busdiv2:
\t         return SystemBusClock/2;
\t      case AdcClockSource_Alt:
\t         return  Osc0Info::getOscerClock();;
\t      case AdcClockSource_Asynch:
\t         return 2000000; // Actually varies with ADLPC/ADHSC
\t      default:
\t         return 0;
\t         break;
\t   }
\t}\n\n

\t/**
\t *  Get input clock frequency for ADC
\t *
\t *  @return Frequency in Hz
\t */
\tstatic unsigned getInputClockFrequency() {
\t   return getInputClockFrequency(static_cast<AdcClockSource>(adc->CFG1 & ADC_CFG1_ADICLK_MASK));
\t}\n\n

//! Default resolution 	static constexpr uint32_t defaultAdcResolution = ADC_CFG1_MODE($(adc_cfg1_mode));


//! Default value for ADCx_CFG1 register 	static constexpr uint32_t cfg1 = 	 ADC_CFG1_ADICLK($(adc_cfg1_adiclk))| 	 ADC_CFG1_MODE($(adc_cfg1_mode))| 	 ADC_CFG1_ADLSMP($(adc_cfg1_adlsmp))| 	 ADC_CFG1_ADIV($(adc_cfg1_adiv))| 	 ADC_CFG1_ADLPC($(adc_cfg1_adlpc));


//! Default value for ADCx_CFG2 register 	 static constexpr uint32_t cfg2 = 	 ADC_CFG2_MUXSEL_MASK | // Choose 'b' channels 	 ADC_CFG2_ADLSTS($(adc_cfg2_adlsts))| 	 ADC_CFG2_ADHSC($(adc_cfg2_adhsc))| 	 ADC_CFG2_ADACKEN($(adc_cfg2_adacken));


//! Default value for ADCx_SC2 register 	static constexpr uint32_t sc2 = 	 ADC_SC2_ACFE($(adc_sc2_acfe))| 	 ADC_SC2_ACFGT($(adc_sc2_acfgt))| 	 ADC_SC2_ACREN($(adc_sc2_acren))| 	 ADC_SC2_REFSEL($(adc_sc2_refsel))| 	 ADC_SC2_DMAEN($(adc_sc2_dmaen))| 	 ADC_SC2_ADTRG($(adc_sc2_adtrg));


//! Default value for ADCx_CV1 register 	static constexpr uint32_t cv1 = $(adc_cv1);


//! Default value for ADCx_CV2 register 	static constexpr uint32_t cv2 = $(adc_cv2);
