for.template:
	/** 	 * Set Continuous input - Mode 2a/b. 	 * Assumes basic configuration done 	 * CR1.EN CR1.WE CR1.SE CR0.FILTER_CNT FPR.FILT_PER 	 * 2a/b Continuous 1 0 0 0 0 COUT == COUTA 	 */ 	%(static)void setInputContinous() %(const){ 	 cmp->CR0 = (cmp->CR0&~CMP_CR0_FILTER_CNT_MASK)|CMP_CR0_FILTER_CNT(0); 	 cmp->CR1 = (cmp->CR1&~(CMP_CR1_SE_MASK|CMP_CR1_WE_MASK))|CmpMode_Direct; 	 cmp->FPR = 0; 	} 	 	/** 	 * Set Sampled, Non-Filtered input - Modes 3a/3b. 	 * 	 * Assumes basic configuration done 	 * The TRGMUX is used as the CmpSampleEnable_External source. 	 * 	 * CmpFilterSamples cmpFilterSamplePeriod 	 * CR1.EN CR1.WE CR1.SE CR0.FILTER_CNT FPR.FILT_PER 	 * 3a Sampled, Non-Filtered, external 1 0 1 1 X COUTA combinational, COUT sampled by external clk pin 	 * 3b Sampled, Non-Filtered, internal 1 0 0 1 >=1 COUTA combinational, COUT sampled by busclk/PFR 	 * 	 * @param[in] CmpSampleEnable Filter clock source 	 * @param[in] cmpFilterSamplePeriod Period of internal sample filter (1..255). 
 	 * Only applicable if CmpSampleEnable=CmpSampleEnable_Internal 	 */ 	%(static)void setInputSampled( 	 CmpSampleEnable CmpSampleEnable, 	 int cmpFilterSamplePeriod=1 	 ) %(const){ 	 usbdm_assert((CmpSampleEnable == CmpSampleEnable_External) || (cmpFilterSamplePeriod>=1), "Illegal parameters"); 	 cmp->CR0 = (cmp->CR0&~CMP_CR0_FILTER_CNT_MASK)|CMP_CR0_FILTER_CNT(1); 	 cmp->CR1 = (cmp->CR1&~(CMP_CR1_SE_MASK|CMP_CR1_WE_MASK))|CmpSampleEnable|CmpWindowEnable_Bypassed; 	 cmp->FPR = cmpFilterSamplePeriod; 	} 	 	/** 	 * Set Sampled, Filtered input - Modes 4a/4b. 	 * 	 * Assumes basic configuration done 	 * The TRGMUX is used as the CmpSampleEnable_External source. 	 * 	 * CmpFilterSamples cmpFilterSamplePeriod 	 * CR1.EN CR1.WE CR1.SE CR0.FILTER_CNT FPR.FILT_PER 	 * 4a Sampled, Filtered, external 1 0 1 >=2 X COUTA combinational, COUT filtered by external clk pin 	 * 4b Sampled, Filtered, internal 1 0 0 >=2 >=1 COUTA combinational, COUT filtered by busclk/PFR 	 * 	 * @param[in] cmpFilterSamples Number samples that must agree before COUT changes (>=2) 	 * @param[in] cmpSampleEnable Filter clock source 	 * @param[in] cmpFilterSamplePeriod Period of internal sample filter (1..255). 
 	 * Only applicable if CmpSampleEnable_Internal 	 */ 	%(static)void setInputFiltered( 	 CmpFilterSamples cmpFilterSamples, 	 CmpSampleEnable cmpSampleEnable=CmpSampleEnable_Internal, 	 uint8_t cmpFilterSamplePeriod=1 	 ) %(const){ 	 usbdm_assert((cmpSampleEnable == CmpSampleEnable_External) || (cmpFilterSamplePeriod>=1), "Illegal parameters"); 	 usbdm_assert((cmpFilterSamples>1) , "Illegal parameter combination"); 	 cmp->CR0 = (cmp->CR0&~CMP_CR0_FILTER_CNT_MASK)|cmpFilterSamples; 	 cmp->CR1 = (cmp->CR1&~(CMP_CR1_SE_MASK|CMP_CR1_WE_MASK))|cmpSampleEnable|CmpWindowEnable_Bypassed; 	 cmp->FPR = cmpFilterSamplePeriod; 	} 	 	/** 	 * Set Windowed input - Modes 5a/5b. 	 * 	 * Assumes basic configuration done 	 * The TRGMUX is used as the Window source. 	 * 	 * CmpFilterSamples cmpFilterSamplePeriod 	 * CR1.EN CR1.WE CR1.SE CR0.FILTER_CNT FPR.FILT_PER 	 * 5a/b Windowed 1 1 0 0 0 COUT == COUTA clocked by bus clock when Window=1 	 */ 	%(static)void setInputWindowed() %(const){ 	 cmp->CR0 = (cmp->CR0&~CMP_CR0_FILTER_CNT_MASK)|0; 	 cmp->CR1 = (cmp->CR1&~(CMP_CR1_SE_MASK|CMP_CR1_WE_MASK))|CmpSampleEnable_Internal|CmpWindowEnable_Enabled; 	 cmp->FPR = 0; 	} 	 	/** 	 * Set Windowed, Re-sampled input - mode 6. 	 * Assumes basic configuration done. 	 * CmpFilterSamples cmpFilterSamplePeriod 	 * CR1.EN CR1.WE CR1.SE CR0.FILTER_CNT FPR.FILT_PER 	 * 6 Windowed, Re-sampled 1 1 0 1 >=1 COUTA clocked by bus clock when Window=1, COUT re-sampled 	 * 	 * @param[in] cmpFilterSamplePeriod Period of internal sample filter (1..255). 	 */ 	%(static)void setInputWindowedResampled( 	 int cmpFilterSamplePeriod=1 	 ) %(const){ 	 usbdm_assert(cmpFilterSamplePeriod>=1, "Illegal parameters"); 	 cmp->CR0 = (cmp->CR0&~CMP_CR0_FILTER_CNT_MASK)|CMP_CR0_FILTER_CNT(1); 	 cmp->CR1 = (cmp->CR1&~(CMP_CR1_SE_MASK|CMP_CR1_WE_MASK))|CmpSampleEnable_Internal|CmpWindowEnable_Enabled; 	 cmp->FPR = cmpFilterSamplePeriod; 	} 	 	/** 	 * Set Windowed, Filtered input - mode 7. 	 * Assumes basic configuration done. 	 * 	 * CmpFilterSamples cmpFilterSamplePeriod 	 * CR1.EN CR1.WE CR1.SE CR0.FILTER_CNT FPR.FILT_PER 	 * 7 Windowed, Filtered 1 1 0 >=2 >=1 COUTA clocked by bus clock when Window=1, COUT filtered by bus clock/PFR 	 * 	 * @param[in] cmpFilterSamples Number samples that must agree before COUT changes (>=2). 	 * @param[in] cmpFilterSamplePeriod Period of internal sample filter (1..255). 	 */ 	%(static)void setInputWindowedFiltered( 	 CmpFilterSamples cmpFilterSamples, 	 int cmpFilterSamplePeriod=1 	 ) %(const){ 	 usbdm_assert(cmpFilterSamplePeriod>=1, "Illegal parameters"); 	 cmp->CR0 = (cmp->CR0&~CMP_CR0_FILTER_CNT_MASK)|cmpFilterSamples; 	 cmp->CR1 = (cmp->CR1&~(CMP_CR1_SE_MASK|CMP_CR1_WE_MASK))|CmpSampleEnable_Internal|CmpWindowEnable_Enabled; 	 cmp->FPR = cmpFilterSamplePeriod; 	}

