peripheralPage.template:
//! Number of DMA channels implemented 	static constexpr unsigned NumChannels = $(NumChannels);
//! Number of DMA vectors implemented 	static constexpr unsigned NumVectors = $(NumVectors);


//! Whether vectors are paired wrt channels i.e. Ch0_Ch16, Ch1_Ch17 etc 	static constexpr bool VectorsPaired = $(NumChannels)>$(NumVectors);

for.values:
dma_csr_bwc;
dma_csr_majorelink;
dma_csr_esg;
dma_csr_dreq;
dma_csr_inthalf;
dma_csr_intmajor;
dma_csr_start

peripheralPage.variableTemplate:
\t   ///  DMA TCD Control and Status
\t   uint32_t %registerName;
\t\n

for.values:
dma_cr_hoe;
dma_cr_clm;
dma_cr_emlm;
dma_cr_GroupArb;
dma_cr_erca;
dma_cr_edbg

dma_cr_hoe                  : SetActionOnError;
                dma_cr_clm                  : SetLinkMode;
                dma_cr_emlm                 : SetMinorLoopMapping;
                dma_cr_erca,dma_cr_GroupArb : SetArbitration;
                dma_cr_edbg                 : SetActionInDebug
