peripheralPage.template:

\t// Sector size for program flash (minimum erase element)
\tstatic constexpr unsigned programFlashSectorSize = $(pflash_sector_size);

\t// Phrase size for program flash (minimum programming element)
\tstatic constexpr unsigned programFlashPhraseSize = $(pflash_phrase_size);

\t// Sector size for data flash (minimum erase element)
\tstatic constexpr unsigned dataFlashSectorSize = $(dflash_sector_size);

\t// Phrase size for data flash (minimum programming element)
\tstatic constexpr unsigned dataFlashPhraseSize = $(dflash_phrase_size);\n\n

\tstruct EepromSizes { \t const uint16_t size; // EEPROM size \t const uint8_t value; // Value to select size \t};
\t/** EEPROM Data Set Size Field */ \tstatic constexpr EepromSizes eepromSizes[] = { \t // Size Value \t { 32, 0x09, }, \t { 64, 0x08, }, \t { 128, 0x07, }, \t { 256, 0x06, }, \t { 512, 0x05, }, \t { 1024, 0x04, }, \t { 2048, 0x03, }, \t { 4096, 0x02, }, \t};
\tstruct PartitionInformation { \t const uint32_t flashSize; //! Remaining data flash \t const uint32_t eeepromSize; //! Flash allocated to EEPROM backing store \t const uint8_t value; //! Partition value \t};
\t/** Flash partition information */ \tstatic constexpr PartitionInformation partitionInformation[] { \t // Flash Backing Value \t { 64*1024, 0*1024 , 0xFF}, \t { 32*1024, 32*1024 , 0x09}, \t { 0*1024, 64*1024, 0x08}, \t};
\t/** Selects EEPROM size */ \tenum EepromSel { \t EepromSel_32Bytes, \t EepromSel_64Bytes, \t EepromSel_128Bytes, \t EepromSel_256Bytes, \t EepromSel_512Bytes, \t EepromSel_1KBytes, \t EepromSel_2KBytes, \t EepromSel_4KBytes, \t};
\t/** Selects division of FlexNVM between flash and EEPROM backing storage */ \tenum PartitionSel { \t PartitionSel_flash64K_eeprom0K, \t PartitionSel_flash32K_eeprom32K, \t PartitionSel_flash0K_eeprom64K, \t \t // All EEPROM \t PartitionSel_flash0K_eeprom_all = PartitionSel_flash0K_eeprom64K, \t};
\t/** \t * Selects division of the EEPROM into two regions \t * This allows A/B regions to have different wear characteristics \t */ \tenum SplitSel { \t SplitSel_A1_B7_8ths = 0x00, //! A=1/8, B=7/8 \t SplitSel_A2_B6_8ths = 0x10, //! A=2/8=1/4, B=6/8=3/4 \t SplitSel_A4_B4_8ths = 0x30, //! A=4/8=1/2, B=4/8=1/2 \t SplitSel_A1_B3_4ths = SplitSel_A2_B6_8ths, //! A=2/8=1/4, B=6/8=3/4 \t SplitSel_A1_B1_2ths = SplitSel_A4_B4_8ths, //! A=4/8=1/2, B=4/8=1/2 \t \t // Default - A = B = 1/2 \t SplitSel_default=SplitSel_A4_B4_8ths, //! Equal partitions \t};
\t//! FlexNVM - EEPROM size \tstatic constexpr EepromSel eepromSel = $(EepromSize);
\t//! FlexNVM - Flash EEPROM partition \tstatic constexpr PartitionSel partitionSel = $(FlashPartition);
\t//! FlexNVM - EEPROM partition \tstatic constexpr SplitSel partitionSplit = $(FlexNVM_Partition);\n\n
