peripheralPage.template:

\t// Sector size for program flash (minimum erase element)
\tstatic constexpr unsigned programFlashSectorSize = $(pflash_sector_size);

\t// Phrase size for program flash (minimum programming element)
\tstatic constexpr unsigned programFlashPhraseSize = $(pflash_phrase_size);

\t// Sector size for data flash (minimum erase element)
\tstatic constexpr unsigned dataFlashSectorSize = $(dflash_sector_size);

\t// Phrase size for data flash (minimum programming element)
\tstatic constexpr unsigned dataFlashPhraseSize = $(dflash_phrase_size);\n\n

\tstruct EepromSizes { \t const uint16_t size; // EEPROM size \t const uint8_t value; // Value to select size \t};
\t/** EEPROM Data Set Size Field */ \tstatic constexpr EepromSizes eepromSizes[] = { \t // Size Value \t { 32, 0x09, }, \t { 64, 0x08, }, \t { 128, 0x07, }, \t { 256, 0x06, }, \t { 512, 0x05, }, \t { 1024, 0x04, }, \t { 2048, 0x03, }, \t};
\tstruct PartitionInformation { \t const uint32_t flashSize; //! Remaining data flash \t const uint32_t eeepromSize; //! Flash allocated to EEPROM backing store \t const uint8_t value; //! Partition value \t};
\t/** Flash partition information */ \tstatic constexpr PartitionInformation partitionInformation[] { \t // Flash Backing Value \t { 32*1024, 0*1024 , 0xFF}, \t { 24*1024, 8*1024 , 0x01}, \t { 16*1024, 16*1024, 0x0A}, \t { 8*1024, 24*1024, 0x09}, \t { 0*1024, 32*1024, 0x08}, \t};
\t/** Selects EEPROM size */ \tenum EepromSel { \t EepromSel_32Bytes, \t EepromSel_64Bytes, \t EepromSel_128Bytes, \t EepromSel_256Bytes, \t EepromSel_512Bytes, \t EepromSel_1KBytes, \t EepromSel_2KBytes, \t};
\t/** Selects division of FlexNVM between flash and EEPROM backing storage */ \tenum PartitionSel { \t PartitionSel_flash32K_eeprom0K, \t PartitionSel_flash24K_eeprom8K, \t PartitionSel_flash16K_eeprom16K, \t PartitionSel_flash8K_eeprom24K, \t PartitionSel_flash0K_eeprom32K, \t \t // All EEPROM \t PartitionSel_flash0K_eeprom_all = PartitionSel_flash0K_eeprom32K, \t};
\t/** \t * Selects division of the EEPROM \t * Not supported on this device \t */ \tenum SplitSel { \t // Not supported \t SplitSel_disabled=0x30, \t};
\t//! FlexNVM - EEPROM size \tstatic constexpr EepromSel eepromSel = $(EepromSize);
\t//! FlexNVM - Flash EEPROM partition \tstatic constexpr PartitionSel partitionSel = $(FlashPartition);
\t//! FlexNVM - EEPROM partition - not available \tstatic constexpr SplitSel partitionSplit = SplitSel_disabled;\n\n
