peripheralPage.template:

\t/** Callback function for ISR */
\tstatic CallbackFunction sCallback;
\t
\t/**
\t * Callback to catch unhandled interrupt
\t */
\tstatic void unhandledCallback($(irq_dummy_parameters)) {
\t   setAndCheckErrorCode(E_NO_HANDLER);
\t}
\t\n


\t/**
\t * Set interrupt callback function.
\t *
\t * @param[in]  ftmCallback Callback function to execute on interrupt
\t */
\tstatic void setCallback(CallbackFunction ftmCallback) {
\t   if (ftmCallback == nullptr) {
\t      ftmCallback = unhandledCallback;
\t   }
\t   usbdm_assert(
\t         (sCallback == unhandledCallback) || (sCallback == ftmCallback),
\t         "Handler already set");
\t   sCallback = ftmCallback;
\t}
\t
\t/**
\t * $(_BASENAME) interrupt handler -  Calls $(_BASENAME) callback
\t */
\tstatic void irqHandler() {
\t
\t   //.....IRQ handler code here..........
\t
\t   // Execute call-back
\t   sCallback($(irq_call));
\t   return;
\t}
\t\n

\t/**
\t * Configure with default settings.
\t * Configuration determined from Configure.usbdmProject
\t */
\tstatic inline void defaultConfigure() {
\t
\t   // Update settings
\t   configure(Info::DefaultInitValue);
\t}
\t\n

\t   // ..........  Regs to init .......... ;
\t   ftm->SC    = init.sc;
\t   ftm->CNT    = init.cnt;
\t   ftm->MOD    = init.mod;
\t   ftm->CNTIN    = init.cntin;
\t   ftm->STATUS    = init.status;
\t   ftm->MODE    = init.mode;
\t   ftm->SYNC    = init.sync;
\t   ftm->OUTINIT    = init.outinit;
\t   ftm->OUTMASK    = init.outmask;
\t   ftm->COMBINE    = init.combine;
\t   ftm->DEADTIME    = init.deadtime;
\t   ftm->EXTTRIG    = init.exttrig;
\t   ftm->POL    = init.pol;
\t   ftm->FMS    = init.fms;
\t   ftm->FILTER    = init.filter;
\t   ftm->FLTCTRL    = init.fltctrl;
\t   ftm->CONF    = init.conf;
\t   ftm->FLTPOL    = init.fltpol;
\t   ftm->SYNCONF    = init.synconf;
\t   ftm->INVCTRL    = init.invctrl;
\t   ftm->SWOCTRL    = init.swoctrl;
\t   ftm->PWMLOAD    = init.pwmload;
\t}
\t\n

