for.setTemplate:
\t/**
\t * Stop timer counter.
\t * This simply disables the counter clock source. \n
\t * To restart use setClockSource() or configure();
\t *
\t * @note This function will affect all channels of the timer.
\t * @note A illegal access trap will occur if the timer has not been enabled
\t */
\t%(static)void stopCounter()%(const) {
\t  %register = (%register&~%mask1);
\t}
\t
\t/**
\t * Set %description0 and %description1
\t *
%comments
\t */
\t%(static)void selectClock(
%params)%(const) {
\t
\t   // Calculate new SC value
\t   uint32_t %registerName = (%register&~%mask)|(%paramExpression);

\t   // Disable timer to change clock (unable to switch directly between clock sources)
\t   %register=0;

\t   // Make sure write has completed (disabled)
\t   (void)(%register);

\t   // Write new value
\t   %register = %registerName;
\t}\n\n

for.values:
ftm_sc_clks  : setClockSource;
                 ftm_sc_ps    : setPrescaler;
                 ftm_sc_mode  : setMode

for.setTemplate:
\t/** \t * Set %description \t * %comments \t * \t * @note This function will affect all channels of the timer. \t * @note The timer will be disabled while making changes. \t */ \t%(static)void %(n)(%params)%(const) { \t \t // Calculate new SC value \t uint32_t %registerName = (%register&~%mask)|(%paramExpression);
\t // Disable timer to change clock (unable to switch directly between clock sources) \t %register=0;
\t // Make sure write has completed (disabled) \t (void)(%register);
\t // Write new value \t %register = %registerName; \t}\n\n

\t/**
\t * Set maximum value of timer counter.
\t *
\t * @param[in] endValue Modulo value in ticks (<65535), 0 = 65536.
\t * @param[in] suspend  Whether to suspend timer during change.
\t *
\t * @note This function will affect all channels of the timer.
\t * @note The timer may be disabled while making changes.
\t * @note This value is write-buffered and updated by MOD synchronisation
\t *       unless suspend is true.
\t */
\t%(static)void setCounterMaximumValue(Ticks endValue, bool suspend=false)%(const) {
\t   uint32_t %registerName1;
\t   if (suspend) {
\t      // Disable timer so register changes are immediate
\t      sc = %register1;
\t      %register1 = 0;
\t      (void)(%register1);
\t   }
\t   %register0 = (unsigned)endValue;
\t   if (suspend) {
\t      %register1 = sc;
\t   }
\t}
\t/**
\t * Get maximum value of timer counter.
\t *
\t * @return Counter modulo value in ticks (<65535), 0 = 65536.
\t */
\t%(static)Ticks getCounterMaximumValue()%(const) {
\t   return Ticks((unsigned)(ftm->MOD));
\t}\n\n

\t/**
\t * Set starting value for counter (CNTIN)
\t *
\t * This value is write-buffered and updated by CNTIN synchronisation unless suspend is true.
\t *
\t * @param[in] startValue  Starting value in ticks (<65535)
\t * @param[in] suspend     Whether to suspend FTM during change.
\t *
\t * @note This function will affect all channels of the timer.
\t */
\t%(static)void setCounterStartValue(Ticks startValue, bool suspend=false)%(const) {
\t   uint32_t sc;
\t   if (suspend) {
\t      sc = ftm->SC;
\t      ftm->SC = 0;
\t   }
\t   ftm->CNTIN = (unsigned)startValue;
\t   if (suspend) {
\t      ftm->SC = sc;
\t   }
\t}
\t
\t/**
\t * Get start value of timer counter.
\t *
\t * @return Counter start value in ticks
\t */
\t%(static)Ticks getCounterStartValue()%(const) {
\t   return Ticks((unsigned)(ftm->CNTIN));
\t}\n\n

fragment.setTemplate:
\t/**
\t * Configure FTM from values specified in init
\t
\t * @param init Class containing initialisation values
\t */
\tstatic ErrorCode configure(const typename Info::Init &init) {
\t
\t   // Enable peripheral clock and map pins
\t   enable();
\t
\t   if constexpr (Info::irqHandlerInstalled) {
\t      // Only set call-back if feature enabled and non-null
\t      if (init.callbackFunction != nullptr) {
\t         setCallback(init.callbackFunction);
\t      }
\t      enableNvicInterrupts(init.irqlevel);
\t   }
\t   uint8_t  sc    = init.sc;
\t   uint16_t cntin = init.cntin;
\t   uint16_t mod   = init.mod;
\t
\t   if (init.modperiod != 0) {
\t
\t      // Calculate sc.ps, mod (assumes cntin=0)
\t      ErrorCode rc = calculateTimingParameters(init.modperiod, sc, mod);
\t      if (rc != E_NO_ERROR) {
\t         return rc;
\t      }
\t      // Configure for modulo operation
\t      cntin = 0;
\t   }
\t   if (init.sc&FtmMode_FreeRunning) {
\t      // Make free-running
\t      cntin = 0;
\t      mod   = 0;
\t   }
\t   // Disable timer to change clock (unable to switch directly between clock sources)
\t   ftm->SC  = 0;
\t         
\t   // Start value for counter
\t   ftm->CNTIN = cntin;
\t   
\t   // End value for counter
\t   ftm->MOD = mod;
\t   
\t   // Restart counter
\t   ftm->CNT = 0;
\t
\t   // Configure timer
\t   ftm->SC  = sc;
\t
\t   return E_NO_ERROR;
\t}\n\n

\t   /**
\t    * Constructor for %description
\t    *
\t    * @tparam   Types
\t    * @param    rest
%comments
\t    */
\t   template <typename... Types>
\t   constexpr Init(
%params, Types... rest) : Init(rest...) {
\t   
\t      cntin = 0;
\t      mod   = 0;
\t      %registerName = %paramExpression;
\t   }
\t\n

fragment.template:
\t/**
\t * Configure Channel from values specified in channelInit
\t
\t * @param channelInit Class containing initialisation values
\t */
\tstatic ErrorCode configure(const typename Info::ChannelInit &channelInit) {
\t
\t   // Enable peripheral clock
\t   Info::enableClock();
\t
\t   // Configure timer combin mode
\t   if ((channelInit.channel&0b1) == 0) {
\t      // Even channel value controls paired channels n,n+1
\t      const unsigned offset = 4*channelInit.channel;
\t      const uint32_t mask = 0xFF<<offset;
\t      ftm->COMBINE = (ftm->COMBINE & ~mask) | (((channelInit.cnsc>>8)<<offset)&mask);
\t   }
\t   // Configure timer channel
\t   ftm->CONTROLS[channelInit.channel].CnV  = channelInit.cnv;
\t   ftm->CONTROLS[channelInit.channel].CnSC = channelInit.cnsc;
\t
\t   return E_NO_ERROR;
\t}\n\n

\t/**
\t * Configure QuadDecoder from values specified in init
\t
\t * @param init Class containing initialisation values
\t */
\tstatic ErrorCode configure(const typename Info::QuadInit &quadInit) {
\t
\t   // Assertions placed here so only checked if FtmQuadDecoder actually used
\t   static_assert(Info::InfoQUAD::info[0].gpioBit >= 0, "FtmQuadDecoder PHA is not mapped to a pin - Modify Configure.usbdm");
\t   static_assert(Info::InfoQUAD::info[1].gpioBit >= 0, "FtmQuadDecoder PHB is not mapped to a pin - Modify Configure.usbdm");
\t
\t   // Enable peripheral clock and map pins
\t   enable();
\t
\t   if constexpr (Info::irqHandlerInstalled) {
\t      // Only set call-back if feature enabled and non-null
\t      if (quadInit.callbackFunction != nullptr) {
\t         FtmBase_T<Info>::setCallback(quadInit.callbackFunction);
\t      }
\t      enableNvicInterrupts(quadInit.irqlevel);
\t   }
\t
\t   // Disable timer to change clock (unable to switch directly between clock sources)
\t   ftm->SC  = 0;
\t         
\t   // Start value for counter
\t   ftm->CNTIN = 0;
\t   
\t   // End value for counter
\t   ftm->MOD =0;
\t   
\t   // Restart counter
\t   ftm->CNT = 0;
\t
\t   // Configure timer
\t   ftm->FILTER = quadInit.qdfilter;
\t   ftm->SC     = quadInit.sc;
\t   ftm->QDCTRL = quadInit.qdctrl;
\t
\t   return E_NO_ERROR;
\t}\n\n
