peripheralPage.template:
	//! Timer external input frequency 
 	static constexpr uint32_t ftmExternalClock = $(ftmExternalClock);


	//! Default Timer Modulo
 	static constexpr uint32_t modulo = FTM_MOD_MOD($(ftm_mod));


	//! Default value for SC register
 	static constexpr uint32_t sc = 	 FTM_SC_CPWMS($(ftm_sc_cpwms))| // Centre-Aligned PWM Select 	 FTM_SC_CLKS($(ftm_sc_clks))| // Clock Mode Selection 	 FTM_SC_TOIE($(ftm_sc_toie))| // Timer Overflow Interrupt Enable 	 FTM_SC_PS($(ftm_sc_ps)); // Prescale Factor Selection

\t/** Minimum resolution for PWM interval */
\tstatic constexpr uint32_t minimumResolution=$(minimumResolution);\n\n

\t/** Minimum usable interval in ticks */      
\tstatic constexpr uint32_t minimumInterval=$(minimumInterval);\n\n

\t/**
\t * Get input clock frequency
\t *
\t * @return Input clock frequency as a uint32_t in Hz
\t */
\tstatic uint32_t getInputClockFrequency() {
\t
\t   switch(ftm().SC&FTM_SC_CLKS_MASK) {
\t   default:
\t   case FTM_SC_CLKS(0): return 0;
\t   case FTM_SC_CLKS(1): return SystemBusClock;
\t   case FTM_SC_CLKS(2): return SystemMcgffClock;
\t   case FTM_SC_CLKS(3): return ftmExternalClock;
\t   }
\t}\n\n
