unsigned    : i2c_speed                                            :   0      ;
               %baseType   : i2c_a1_ad                                            :   0      ;
               %baseType   : i2c_f_mult,i2c_f_icr                                 :   0      ;
               %baseType   : i2c_c1_iicen,i2c_c1_iicie,i2c_c1_mst,i2c_c1_tx,
