\t * (Individual to each peripheral)
\t *
\t * @return Input clock frequency as a uint32_t in Hz
\t */
\tvirtual uint32_t getInputClockFrequency() const override {
\t   return $(ClockSource);
\t}
\t\n


\t/*
\t *   Default Destructor
\t */
\tvirtual ~$(_Info)() = default;
\t
