\t   defaultConfigure();
\t}
\t
\t/*
\t *   Constructor
\t */
\t$(_Info)(const Init &init) : $(_BasicInfo)($(_basename)) {
\t   thisPtr = this;
\t   configure(init);
\t}
\t\n


