devicePage.template:
	//! Default Timer Compare value
 	static constexpr uint32_t cmr = $(lptmr_cmr);


	//! Default PSR value
 	static constexpr uint32_t psr = 	 LPTMR_PSR_PRESCALE($(lptmr_psr_prescaler))| 	 LPTMR_PSR_PBYP($(lptmr_psr_pbyp))| 	 LPTMR_PSR_PCS($(lptmr_psr_pcs));

\t/**
\t * Gets input clock frequency of LPTMR
\t *
\t * @return Frequency as an uint32_t in Hz
\t */
\tstatic uint32_t getInputClockFrequency() {
\t   /** Pointer to hardware */
\t   static constexpr volatile LPTMR_Type *lptmr = reinterpret_cast<volatile LPTMR_Type *>(basePtr);
\t
\t   switch(lptmr->PSR&LPTMR_PSR_PCS_MASK) {
\t   default:
\t   case LPTMR_PSR_PCS(0): return SystemMcgirClock;
\t   case LPTMR_PSR_PCS(1): return SystemLpoClock;
\t   case LPTMR_PSR_PCS(2): return SystemErclk32kClock;
\t   case LPTMR_PSR_PCS(3): return SystemOscerClock;
\t   }
\t}\n\n

\t/**
\t * Gets clock frequency of LPTMR
\t *
\t * @return Frequency as a float in Hz
\t */
\tstatic float getClockFrequencyF() {
\t   /** Pointer to hardware */
\t   static constexpr volatile LPTMR_Type *lptmr = reinterpret_cast<volatile LPTMR_Type *>(basePtr);
\t
\t   float freq = getInputClockFrequency();
\t   if (lptmr->PSR&LPTMR_PSR_PBYP_MASK) {
\t      return freq;
\t   }
\t   return freq/(1<<(((lptmr->PSR&LPTMR_PSR_PRESCALE_MASK)>>LPTMR_PSR_PRESCALE_SHIFT)+1));
\t}\n\n

\t/**
\t * Gets clock frequency of LPTMR
\t *
\t * @return Frequency as a uint32_t in Hz (may underflow)
\t */
\tstatic uint32_t getClockFrequency() {
\t   /** Pointer to hardware */
\t   static constexpr volatile LPTMR_Type *lptmr = reinterpret_cast<volatile LPTMR_Type *>(basePtr);
\t
\t   uint32_t freq = getInputClockFrequency();
\t   if (lptmr->PSR&LPTMR_PSR_PBYP_MASK) {
\t      return freq;
\t   }
\t   return freq/(1<<(((lptmr->PSR&LPTMR_PSR_PRESCALE_MASK)>>LPTMR_PSR_PRESCALE_SHIFT)+1));
\t}\n\n
