peripheralPage.template:
	//! Default Baud rate used if not explicitly given 	static constexpr uint32_t defaultBaudRate = $(defaultBaudRate);


	//! Default buffer size for receive queue when interrupt driven 	static constexpr unsigned receiveBufferSize = $(receiveBufferSize);


	//! Default buffer size for transmit queue when interrupt driven 	static constexpr unsigned transmitBufferSize = $(transmitBufferSize);


#if defined(PCC_PCC_LPUART$(_instance)_CGC_MASK) 	//! Default clock source 	static constexpr PccDiv2Clock defaultClockSource = PccDiv2Clock_Firc;

 	/** 	 * Set LPUART$(_instance) input clock source. 	 * 	 * @param pccDiv2Clock Clock source selection 	 * 	 * @note This peripheral uses the DIV2 peripheral clocks e.g. SOSCDIV2_CLK 	 * @note The peripheral bus clock will be disabled before changing the 	 clock and is left disabled. 	 */ 	static void setClockSource(PccDiv2Clock pccDiv2Clock) { 	 PccInfo::setLpuart$(_instance)ClockSource(pccDiv2Clock); 	} #endif


	/** 	 * Get input clock frequency 	 * 	 * @return Input clock frequency as a uint32_t in Hz 	 */ 	static uint32_t getClockFrequency() { #if defined(PCC_PCC_LPUART$(_instance)_CGC_MASK) 	 return PccInfo::getLpuart$(_instance)Frequency(); 	#elif defined(SIM_SOPT2_LPUART$(_instance)SRC_MASK) 	 return SimInfo::getLpuart$(_instance)Clock(); 	#else 	 return SimInfo::getLpuartClock(); #endif 	}
