peripheralPage.template:
\tenum ClockMode {
\t   ClockMode_None     = -1,
\t   ClockMode_LIRC_2M  = 0,
\t   ClockMode_LIRC_8M,
\t   ClockMode_HIRC_48M,
\t   ClockMode_EXT,
\t};\n\n

\t//! Frequency of Slow Internal Reference Clock [~2MHz]
\tstatic constexpr uint32_t system_slow_lirc_clock = $(system_slow_irc_clock)UL;\n\n

\t//! Frequency of Fast Internal Reference Clock [~8MHz]
\tstatic constexpr uint32_t system_fast_lirc_clock = $(system_fast_irc_clock)UL;\n\n

\t//! Frequency of High Speed Internal Reference Clock [~48MHz]
\tstatic constexpr uint32_t system_hirc_clock      = $(system_irc48m_clock)UL;\n\n

\t//! Structure for clock configurations
\tstruct ClockInfo {
\t   //! System Clock Divider Register 1
\t   const uint32_t clkdiv1;\n\n

\t   //! SIM SOPT2 - Clock selectors for various peripherals
\t   const uint32_t sopt2;\n\n

\t   //! Clock Mode
\t   const ClockMode clockMode:8;\n\n

\t   //! Control Register 1 - IRCLKEN, IREFSTEN, (-CLKS)
\t   const uint8_t c1;
\t   //! Control Register 2 - RANGE0, HGO0, EREFS0, IRCS
\t   const uint8_t c2;
\t   //! Status and Control Register - FCRDIV
\t   const uint8_t sc;
\t   //! Miscellaneous Control Register - HIRCEN, LIRC_DIV2 (-HIRCLPEN)
\t   const uint8_t mc;
\t};\n\n

\t/**
\t * Get HIRC_CLK
\t *
\t * @return frequency in Hz as uint32_t
\t */
\tstatic uint32_t getMcgPClk() {
\t   if ((mcg().MC&MCG_MC_HIRCEN_MASK) || ((mcg().S&MCG_S_CLKST_MASK) == MCG_S_CLKST(0))) {
\t      return system_hirc_clock;
\t   }
\t   else {
\t      return 0;
\t   }
\t}\n\n

\t/**
\t * Get LIRC_CLK
\t *
\t * @return frequency in Hz as uint32_t
\t */
\tstatic uint32_t getLircClk() {
\t   if ((mcg().C1&MCG_C1_IRCLKEN_MASK) || ((mcg().S&MCG_S_CLKST_MASK) == MCG_S_CLKST(1))) {
\t      if (mcg().C2&MCG_C2_IRCS_MASK) {
\t         return system_fast_lirc_clock;
\t      }
\t      else {
\t         return system_slow_lirc_clock;
\t      }
\t   }
\t   else {
\t      return 0;
\t   }
\t}\n\n

\t/**
\t * Get LIRC_DIV1_CLK
\t *
\t * @return frequency in Hz as uint32_t
\t */
\tstatic uint32_t getLircDiv1Clk() {
\t   return getLircClk()/(1<<((mcg().SC&MCG_SC_FCRDIV_MASK)>>MCG_SC_FCRDIV_SHIFT));
\t}
\t
\t/**
\t * Get MCGIRCLK
\t *
\t * @return frequency in Hz as uint32_t
\t */
\tstatic uint32_t getMcgIrClock() {
\t   return getLircDiv1Clk()/(1<<((mcg().MC&MCG_MC_LIRC_DIV2_MASK)>>MCG_MC_LIRC_DIV2_SHIFT));
\t}\n\n

\t/**
\t * Get MCGOUTCLK
\t *
\t * @return frequency in Hz as uint32_t
\t */
\tstatic uint32_t getMcgOutClock() {
\t   switch (mcg().S&MCG_S_CLKST_MASK) {
\t   case MCG_S_CLKST(0): // HIRC
\t      return getMcgPClk();
\t   case MCG_S_CLKST(1): // LIRC (IRC2Mz/IRC8Mz)/DIV1
\t      return getLircDiv1Clk();
\t   case MCG_S_CLKST(2): // EXT (IRC48MHz)
\t      return system_hirc_clock;
\t   default:
\t   case MCG_S_CLKST(3): // Reserved
\t      return 0;
\t   }
\t}\n\n

	 { // $(ClockConfig.) ($(clock_mode.))
 	 	 //! SIM CLKDIV1 System Clock Divider Register 1 	 SIM_CLKDIV1_OUTDIV4($(/SIM/sim_clkdiv1_outdiv4.:0))| // Flash clock 	 SIM_CLKDIV1_OUTDIV3($(/SIM/sim_clkdiv1_outdiv3.:0))| // FlexBus clock 	 SIM_CLKDIV1_OUTDIV2($(/SIM/sim_clkdiv1_outdiv2.:0))| // Bus clock 	 SIM_CLKDIV1_OUTDIV1($(/SIM/sim_clkdiv1_outdiv1.:0)), // Core/system clock


	#ifdef SIM_CLKDIV3_PLLFLLDIV 	 //! SIM CLKDIV3 System Clock Divider Register 3 	 $(/SIM/sim_clkdiv3_pllfll.:0), 	#endif


	 //! SIM SOPT2 System Options Register 2 	#ifdef SIM_SOPT2_TRACECLKSEL 	 SIM_SOPT2_TRACECLKSEL($(/SIM/sim_sopt2_traceclksel:0)) | // Debug trace clock select 	#endif 	#ifdef SIM_SOPT2_TIMESRC 	 SIM_SOPT2_TIMESRC($(/SIM/sim_sopt2_timesrc:0)) | // IEEE 1588 timestamp clock source select 	#endif 	#ifdef SIM_SOPT2_RMIISRC 	 SIM_SOPT2_RMIISRC($(/SIM/sim_sopt2_rmiisrc:0)) | // Ethernet RMII clock source select 	#endif 	#ifdef SIM_SOPT2_SDHCSRC 	 SIM_SOPT2_SDHCSRC($(/SIM/sim_sopt2_sdhcsrc:0)) | // SDHC clock source select 	#endif 	#ifdef SIM_SOPT2_UART0SRC 	 SIM_SOPT2_UART0SRC($(/SIM/sim_sopt2_uart0src:0)) | // UART0 clock source select 	#endif 	#ifdef SIM_SOPT2_FLEXIOSRC 	 SIM_SOPT2_FLEXIOSRC($(/SIM/sim_sopt2_flexiosrc:0)) | // FLEXIO clock source select 	#endif 	#ifdef SIM_SOPT2_LPUARTSRC 	 SIM_SOPT2_LPUARTSRC($(/SIM/sim_sopt2_lpuartsrc:0)) | // LPUART clock source select 	#endif 	#ifdef SIM_SOPT2_LPUART0SRC 	 SIM_SOPT2_LPUART0SRC($(/SIM/sim_sopt2_lpuart0src:0)) | // LPUART0 clock source select 	#endif 	#ifdef SIM_SOPT2_LPUART1SRC 	 SIM_SOPT2_LPUART1SRC($(/SIM/sim_sopt2_lpuart1src:0)) | // LPUART1 clock source select 	#endif 	#ifdef SIM_SOPT2_PTD7PAD 	 SIM_SOPT2_PTD7PAD($(/SIM/sim_sopt2_ptd7pad:0)) | // PTD7 pad drive strength 	#endif 	#ifdef SIM_SOPT2_TPMSRC 	 SIM_SOPT2_TPMSRC($(/SIM/sim_sopt2_tpmsrc:0)) | // TPM clock source select 	#endif 	#ifdef SIM_SOPT2_USBSRC 	 SIM_SOPT2_USBSRC($(/SIM/sim_sopt2_usbsrc:0)) | // USB clock source select 	#endif 	#ifdef SIM_SOPT2_FBSL 	 SIM_SOPT2_FBSL($(/SIM/sim_sopt2_fbsl:0)) | // FlexBus security level 	#endif 	 SIM_SOPT2_RTCCLKOUTSEL($(/SIM/sim_sopt2_rtcclkoutsel)) | // RTC clock out select 	 SIM_SOPT2_CLKOUTSEL($(/SIM/sim_sopt2_clkoutsel)), // CLKOUT pin clock source select


	 //! Clock Mode 	 McgInfo::$(clock_mode.),


	 //! Control Register 1 - Excluding CLKS 	 MCG_C1_IRCLKEN($(mcg_c1_irclken.)) | // IRCLEN Internal Reference Clock Enable 	 MCG_C1_IREFSTEN($(mcg_c1_irefsten.)), // IREFSTEN Internal Reference Stop Enable


	 //! Control Register 2 	 MCG_C2_RANGE0($(mcg_c2_range)) | // RANGE Frequency Range Select 	 MCG_C2_HGO0($(mcg_c2_hgo0)) | // HGO High Gain Oscillator Select 	 MCG_C2_EREFS0($(mcg_c2_erefs0)) | // EREFS External Reference Select 	 MCG_C2_IRCS($(mcg_c2_ircs.)), // IRCS Internal Reference Clock Select


	 //! Status and Control Register 	 MCG_SC_FCRDIV($(mcg_sc_fcrdiv.)), // FCRDIV (LIRC_DIV1) Internal Clock Reference Divider 1


	 //! Miscellaneous Control Register - Excluding HIRCLPEN 	 MCG_MC_HIRCEN($(mcg_mc_hircen.)) | // HIRCEN Internal Reference Clock Enable 	 MCG_MC_LIRC_DIV2($(mcg_mc_lirc_div2.)), // LIRC_DIV2 Internal Reference Divider 2

 	 },
