	 //! Control Register 6 - Excluding PLLS 	 MCG_C6_LOLIE0($(mcg_c6_lolie0.)) | // LOLIE0 Loss of Lock interrupt Enable 	 MCG_C6_CME0($(mcg_c6_cme0.)) | // CME0 Clock Monitor Enable 	 MCG_C6_VDIV0($(mcg_c6_vdiv0.)), // VDIV0 PLL VCO Divider


	 //! Status and Control Register 	 MCG_SC_FCRDIV($(mcg_sc_fcrdiv.)), // FCRDIV Internal Clock Reference Divider


	 //! Control Register 7 	 MCG_C7_OSCSEL($(mcg_c7_oscsel.)), // OSCSEL MCG OSC Clock Select


	 //! Control Register 8 	 MCG_C8_LOCRE1($(mcg_c8_locre1.)) | // LOCRE1 RTC Loss of Clock Reset Enable 	 MCG_C8_LOLRE($(mcg_c8_lolre.)) | // LOLRE PLL Loss of Lock Reset Enable 	 MCG_C8_CME1($(mcg_c8_cme1.)), // CME1 Clock Monitor Enable 1
 	 },
