\t/**
\t * Exit %description
\t *
\t */
\tstatic void exitComputeMode() {
\t     
\t   %register = McmComputeOnlyMode_Disable;
\t   while ((%register & MCM_CPO_CPOACK_MASK) != 0) {
\t      __asm__("nop");
\t   }
\t}\n\n
\t/**
\t * Set %description
\t *
%comments
\t */
\tstatic void enterComputeMode(%params) {
\t     
\t   %register = %paramExpression;
\t   while ((%register & MCM_CPO_CPOACK_MASK) == 0) {
\t      __asm__("nop");
\t   }
\t}\n\n
