devicePage.template:
	struct PdbChannel { 	 uint32_t c1; 	 uint32_t dly0; 	 uint32_t dly1; 	};


	// Number of PDB channels 	static constexpr size_t numChannels = 2;


	// Default values for channel registers 	static constexpr PdbChannel pdb_ch[numChannels] = { 	{ // Channel[0] Control Register 1 	 PDB_C1_BB($(pdb_ch0_c1_bb)) | // Pre-Trigger Back-to-Back Operation Enable 	 PDB_C1_TOS($(pdb_ch0_c1_tos)) | // Pre-Trigger Output Select 	 PDB_C1_EN($(pdb_ch0_c1_en)), // Pre-Trigger Enable
 	 PDB_DLY_DLY($(pdb_ch0_dly0)), // Channel[0] pre-trigger delay 0
 	 PDB_DLY_DLY($(pdb_ch0_dly1)), // Channel[0] pre-trigger delay 1
 	}, 	{ // Channel[1] Control Register 1 	 PDB_C1_BB($(pdb_ch1_c1_bb)) | // Channel Pre-Trigger Back-to-Back Operation Enable 	 PDB_C1_TOS($(pdb_ch1_c1_tos)) | // Channel Pre-Trigger Output Select 	 PDB_C1_EN($(pdb_ch1_c1_en)), // Channel Pre-Trigger Enable
 	 PDB_DLY_DLY($(pdb_ch1_dly0)), // Channel[1] pre-trigger delay 0
 	 PDB_DLY_DLY($(pdb_ch1_dly1)), // Channel[1] pre-trigger delay 1
 	}, 	};

	struct PdbDac { 	 uint32_t dacintc; 	 uint32_t dacint; 	};


	// Number of DACs 	static constexpr size_t numDacs = 1;


	// Default values for DAC registers 	static constexpr PdbDac pdb_dac[numDacs] = { 	{ // DAC[0] Interval register 	 PDB_INTC_EXT($(pdb_int0c_ext)) | // DAC External Trigger Input Enable
 	 PDB_INTC_TOE($(pdb_int0c_toe)), // DAC Interval Trigger Enable
 	 PDB_INT_INT($(pdb_int0_int)), // DAC Interval
 	}, 	};

// Number of PDB pulse outputs 	static constexpr size_t numPulseOutputs = 2;


static constexpr uint32_t pdb_podly[numPulseOutputs] = {
 // Pulse Output[0] Delays { 	 PDB_PODLY_DLY1($(pdb_po0_dly_dly1)) | // Delay 1 	 PDB_PODLY_DLY2($(pdb_po0_dly_dly2)), // Delay 2


 // Pulse Output[1] Delays 	 PDB_PODLY_DLY1($(pdb_po1_dly_dly1)) | // Delay 1 	 PDB_PODLY_DLY2($(pdb_po1_dly_dly2)), // Delay 2 	};
