peripheralPage.template:
\t/**
\t * Configure PORT from values specified in init
\t *
\t * @param init Class containing initialisation values
\t */
\tstatic void configure(const typename Info::Init &init) {
\t   enable();
\t
\t   port->IOFLT0    = init.ioflt0;
\t   port->IOFLT1    = init.ioflt1;
\t   port->PUE0    = init.pue0;
\t   port->PUE1    = init.pue1;
\t   port->PUE2    = init.pue2;
\t   port->HDRVE    = init.hdrve;
\t
\t   calibrate();
\t}
\t
\t/**
\t * Configure with default settings.
\t * Configuration determined from Configure.usbdmProject
\t */
\tstatic inline void defaultConfigure() {
\t
\t   // Update settings
\t   configure(Info::DefaultInitValue);
\t}\n

