\t   #endif
\t
\t   SIM->SOPT1 = sopt1;
\t   SIM->SOPT2 = sopt2;
\t   SIM->SOPT4 = sopt4;
\t   SIM->SOPT5 = sopt5;
\t   SIM->SOPT7 = sopt7;
\t   SIM->SOPT8 = sopt8;
\t
\t   SIM->CLKDIV1 = clkdiv1;
\t   SIM->CLKDIV2 = clkdiv2;
\t}
