category.template:
	/** 	 * Select the ADC0 Trigger source in STOP and VLPS modes, or when ADC0 Alternative Trigger is active. 	 */ 	enum SimAdc0Trigger { 	 SimAdc0Trigger_External = SIM_SOPT7_ADC0TRGSEL(0), //!< External Trigger Source 	 SimAdc0Trigger_Cmp0 = SIM_SOPT7_ADC0TRGSEL(1), //!< Comparator 0 	 SimAdc0Trigger_Cmp1 = SIM_SOPT7_ADC0TRGSEL(2), //!< Comparator 1 (if present) 	 SimAdc0Trigger_Cmp2 = SIM_SOPT7_ADC0TRGSEL(3), //!< Comparator 2 (if present) 	 SimAdc0Trigger_PitCh0 = SIM_SOPT7_ADC0TRGSEL(4), //!< PIT Channel 0 	 SimAdc0Trigger_PitCh1 = SIM_SOPT7_ADC0TRGSEL(5), //!< PIT Channel 1 	 SimAdc0Trigger_PitCh2 = SIM_SOPT7_ADC0TRGSEL(6), //!< PIT Channel 2 	 SimAdc0Trigger_PitCh3 = SIM_SOPT7_ADC0TRGSEL(7), //!< PIT Channel 3 	 SimAdc0Trigger_Tpm0 = SIM_SOPT7_ADC0TRGSEL(8), //!< TPM0 Init and Ext Trigger Outputs 	 SimAdc0Trigger_Tpm1 = SIM_SOPT7_ADC0TRGSEL(9), //!< TPM1 Init and Ext Trigger Outputs 	 SimAdc0Trigger_Tpm2 = SIM_SOPT7_ADC0TRGSEL(10), //!< TPM2 Init and Ext Trigger Outputs (if present) 	 SimAdc0Trigger_Tpm3 = SIM_SOPT7_ADC0TRGSEL(11), //!< TPM3 Init and Ext Trigger Outputs (if present) 	 SimAdc0Trigger_RtcAlarm = SIM_SOPT7_ADC0TRGSEL(12), //!< RTC Alarm 	 SimAdc0Trigger_RtcSeconds = SIM_SOPT7_ADC0TRGSEL(13), //!< RTC Seconds 	 SimAdc0Trigger_Lptrm = SIM_SOPT7_ADC0TRGSEL(14), //!< LPTMR 	 SimAdc0Trigger_15 = SIM_SOPT7_ADC0TRGSEL(15), //!< Reserved 	};
	/** 	 * Selects the ADC0 trigger mode.
 	 * 	 * _Tpm - ADC is triggered by TPM. Pre-triggers are TPM channel 0 (A) and channel 1 (B) 	 * _Alt_PreTrigger_0 - ADC is triggered by SimAdc0Trigger selection and uses pretrigger 0 = A (SC1[0]/R[0]) 	 * _Alt_PreTrigger_1 - ADC is triggered by SimAdc0Trigger selection and uses pretrigger 1 = B (SC1[1]/R[1]) 	 */ 	enum SimAdc0TriggerMode { 	 SimAdc0TriggerMode_Tpm = SIM_SOPT7_ADC0ALTTRGEN(0), //!< TPM trigger 	 SimAdc0TriggerMode_Alt_PreTrigger_0 = SIM_SOPT7_ADC0ALTTRGEN(1)|SIM_SOPT7_ADC0PRETRGSEL(0), //!< Pre-trigger 0 = A (SC1[0]) 	 SimAdc0TriggerMode_Alt_PreTrigger_1 = SIM_SOPT7_ADC0ALTTRGEN(1)|SIM_SOPT7_ADC0PRETRGSEL(1), //!< Pre-trigger 1 = B (SC1[1]) 	};

peripheralPage.template:
\t/**
\t * Update system clock values
\t *
\t * @param systemClock Frequency of clock provided to system clock dividers
\t */
\tstatic void updateSystemClocks(unsigned systemClock) {
\t
\t   SystemCoreClock    = systemClock/(((SIM->CLKDIV1&SIM_CLKDIV1_OUTDIV1_MASK)>>SIM_CLKDIV1_OUTDIV1_SHIFT)+1);
\t   SystemBusClock     = SystemCoreClock/(((SIM->CLKDIV1&SIM_CLKDIV1_OUTDIV4_MASK)>>SIM_CLKDIV1_OUTDIV4_SHIFT)+1);
\t}\n\n
