peripheralPage.template:
	//! Timer external input frequency 
 	static constexpr uint32_t tpmExternalClock = $(tpmExternalClock);


	//! Default Timer Modulo
 	static constexpr uint32_t modulo =TPM_MOD_MOD($(tpm_mod));


	//! Default value for SC register
 	static constexpr uint32_t sc = 	 TPM_SC_CPWMS($(tpm_sc_cpwms))| // Centre-Aligned PWM Select 	 TPM_SC_CMOD($(tpm_sc_cmod)) | // Clock Mode Selection 	 TPM_SC_TOIE($(tpm_sc_toie))| // Timer Overflow Interrupt Enable 	 TPM_SC_PS($(tpm_sc_ps)); // Prescale Factor Selection

\t/** Minimum resolution for PWM interval */
\tstatic constexpr uint32_t minimumResolution=$(minimumResolution);\n\n

\t/** Minimum usable interval in ticks */      
\tstatic constexpr uint32_t minimumInterval=$(minimumInterval);\n\n

\t/**
\t * Get input clock frequency
\t *
\t * @return Input clock frequency as a uint32_t in Hz
\t */
\tstatic uint32_t getInputClockFrequency() {
\t
\t   switch(tpm().SC&TPM_SC_CMOD_MASK) {
\t   default:
\t   case TPM_SC_CMOD(0): return 0;
\t   case TPM_SC_CMOD(1): return SimInfo::getTpmClock();
\t   case TPM_SC_CMOD(2): return tpmExternalClock;
\t   case TPM_SC_CMOD(3): return 0;
\t   }
\t}\n\n
