peripheralPage.template:
\t//! Timer external input frequency \tstatic constexpr uint32_t tpmExternalClock = $(tpmExternalClock);
\t//! Default Timer Modulo \tstatic constexpr uint32_t modulo =TPM_MOD_MOD($(tpm_mod));
\t//! Default value for SC register \tstatic constexpr uint32_t sc = \t TPM_SC_CPWMS($(tpm_sc_cpwms))| // Centre-Aligned PWM Select \t TPM_SC_CMOD($(tpm_sc_cmod)) | // Clock Mode Selection \t TPM_SC_TOIE($(tpm_sc_toie))| // Timer Overflow Interrupt Enable \t TPM_SC_PS($(tpm_sc_ps)); // Prescale Factor Selection \n\n

\t/** Minimum resolution for PWM interval */
\tstatic constexpr uint32_t minimumResolution=$(minimumResolution);

\t/** Minimum usable interval in ticks */      
\tstatic constexpr uint32_t minimumInterval=$(minimumInterval);

\t/**
\t * Get input clock frequency
\t *
\t * @return Input clock frequency as a uint32_t in Hz
\t */
\tstatic uint32_t getInputClockFrequency() {
\t
\t   switch(tpm->SC&TPM_SC_CMOD_MASK) {
\t   default:
\t   case TPM_SC_CMOD(0): return 0;
\t   case TPM_SC_CMOD(1): return SimInfo::getTpmClock();
\t   case TPM_SC_CMOD(2): return tpmExternalClock;
\t   case TPM_SC_CMOD(3): return 0;
\t   }
\t}\n\n
