/** 	 * Select trigger source for FTM$(instance) Hardware trigger 	 * 	 * @param[in] trgmuxSelection Trigger source selection 	 * @param[in] trgmuxLock Lock trigger settings. This is once-off operation. 	 */ 	static void setFtm$(instance)HardwareTrigger(TrgmuxSelection trgmuxSelection, TrgmuxLock trgmuxLock=TrgmuxLock_Off) { 	 trgmux().TRGMUX_FTM$(instance) = (trgmux().TRGMUX_FTM$(instance) & ~TRGMUX_TRGMUXn_SEL0_MASK ) | 	 TRGMUX_TRGMUXn_SEL0(trgmuxSelection) | trgmuxLock; 	}


