fragment.template:

\t/**
\t * $(_Class) interrupt handler
\t * Passes control to call-back function
\t */
\tstatic void irqHandler() {
\t
\t   // Capture status
\t   uint8_t status = tsi->GENCS&(TSI_GENCS_OUTRGF_MASK|TSI_GENCS_EOSF_MASK);
\t   tsi->GENCS = tsi->GENCS | status;
\t
\t   // Execute call-back
\t   sCallback(status);
\t}
\t\n

